import React from 'react';
import { Link } from "react-router-dom";
import {Row, Col} from "react-bootstrap";
import Button from 'react-bootstrap/Button';

class ItemList extends React.Component {

    setEditedListId(id) {
        this.props.setEditedListId(id)
        // console.log("My new ID: ",id)
    }

    handleSubmit(id, text) {
        this.props.deleteItem(id, text);
    }

    handleListDelete(id) {
        this.props.deleteList(id)
    }

     render() {
        let listJsx = (
            this.props.lists.map((l) => (
                <Col sm='4' key={l.title}>
                    <div className="background">
                        <h3>{l.title}</h3>

                            <div className="white">
                                <Button variant="info" onClick={() => this.setEditedListId(l.id)}><Link to={'/additem/' + l.id}>Add Item</Link></Button>
                                <Button variant="info"><Link to={'/editlist/' + l.id}>Edit</Link></Button>
                                <Button variant="info" onClick={() => this.handleListDelete(l.id)}>                                        <span role="img">❌</span>❎</Button>
                            </div>
                        </div>

                        {/* <ul>
                            {l.items.map((i) => (
                                <div className="left">
                                    <li key={i}>
                                        {i}
                                        <Button className="btn-sm"
                                        variant="outline-info"
                                        onClick={(e) => this.props.deleteItem(l.id, i)}>
                                        <span role="img">❌</span>
                                        </Button>
                                    </li>
                                </div>
                            ))}
                        </ul> */}
                </Col>
                ))
            ) 

        return (
            <div className={this.props.showLogIn ? 'false' : 'true'}>
                {/* <h2>Item List</h2> */}
                <Row>
                    {listJsx}
                </Row>
            </div>
        )
    }
}

export default ItemList;
