                <div>
                  <Route path='/login'>
                    <ValidatedLoginForm />
                  </Route>
                  <Route path='/sign-up'>
                    <SignUp />
                  </Route>
                </div>
              </div>

              <Error404View />

            </Switch>
      </div>
      </Router>
    );
