class Home extends React.Component {
  render() {
    return(
      <div className="text-center">
        <h1>Welcome</h1>
        <div>
              <Button variant="info"><Link to="/login">SIGN IN</Link></Button>
              <Button variant="info"><Link to="/sign-up">SIGN UP</Link></Button>
        </div>
      </div>
    )
  }
}

